            if ($PSCmdlet.ShouldProcess("vpnalwaysonprofile", "Add SSL VPN configuration Object")) {
                $result = Invoke-ADCNitroApi -ADCSession $ADCSession -Method POST -Type vpnalwaysonprofile -Payload $Payload -GetWarning
