            if ($PSBoundParameters.ContainsKey('bindpoint')) { $Payload.Add('bindpoint', $bindpoint) }
 
            if ($PSCmdlet.ShouldProcess("vpnvserver_vpnsessionpolicy_binding", "Add SSL VPN configuration Object")) {
